    public void converttxt(){
        for(int audiocounter=0;audiocounter<mThumbIds.length;audiocounter++) {
            for (int m = 0; m < mThumbIds[audiocounter].length; m++) {
                String hex = mThumbIds[audiocounter][m];
                ByteBuffer buff = ByteBuffer.allocate(hex.length() / 2);
                for (int i = 0; i < hex.length(); i += 2) {
                    buff.put((byte) Integer.parseInt(hex.substring(i, i + 2), 16));
                }
                buff.rewind();
                Charset cs = Charset.forName("UTF-8");
                CharBuffer cb = cs.decode(buff);
                String enstrcb = cb.toString();
                mThumbIds[audiocounter][m] = enstrcb;
            }
        }
    }
